package day05;

public class DoWhileLoopExamples {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 1;
		
		do {
			
			System.out.println("First 10 Natural Numbers :"+i);
			i++;
		}while(i<=10);
		
		// First 10 Natural number descending Order
		
		 i =10;
		
		do {
			System.out.println("First 10 natural numbers : "+i);
			i--;
		} while (i>0);
	}

}
