package day1;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int  a =100;
		System.out.println("Value of a is:"+a);
		
		byte age =32;
		System.out.println("age of the person is :"+age);
		
		long val = 12312312312341223l;
		System.out.println("value of the long val is:"+val);
		
		float price = 123.5f;
		System.out.println("Price of the item is:"+price);
		
		char grade ='A';
		System.out.println("Grade of the student is :"+grade);
		
		boolean valu = false;
		System.out.println("bool val is:"+valu);
		
		String stu_name ="Rajendra";
		System.out.println("Student name is:"+stu_name);
	}

}
